import React, { useEffect, useState } from 'react';
import { IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import { getAllItems, updateItem, deleteItem } from '../../../api';

function List() {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    loadItems();
  }, []);

  const loadItems = async () => {
    try {
      const response = await getAllItems();
      setTodos(response.data);
    } catch (error) {
      console.error("There was an error fetching the items!", error);
    }
  };

  const toggleComplete = async (index) => {
    const todo = todos[index];
    const updatedTodo = { ...todo, completed: !todo.completed };

    try {
      const response = await updateItem(todo.id, updatedTodo);
      const newTodos = todos.map((item, i) => (i === index ? response.data : item));
      setTodos(newTodos);
    } catch (error) {
      console.error("There was an error updating the item!", error);
    }
  };

  const handleDelete = async (index) => {
    const todo = todos[index];

    try {
      await deleteItem(todo.id);
      const newTodos = todos.filter((_, i) => i !== index);
      setTodos(newTodos);
    } catch (error) {
      console.error("There was an error deleting the item!", error);
    }
  };

  return (
    <div id='list_container' className='list-group'>
      <ul>
        {todos.map((todo, i) => {
          const id = `checkbox${i}`;
          return (
            <li key={i} className={`list-group-item ${todo.completed ? 'completed' : ''} d-flex justify-content-between align-items-center`}>
              <div>
                <input
                  className="form-check-input me-1"
                  type="checkbox"
                  id={id}
                  onChange={() => toggleComplete(i)}
                  checked={todo.completed}
                />
                <label className="form-check-label" htmlFor={id}>
                  {todo.description}
                </label>
              </div>
              <IconButton aria-label="delete" size="large" onClick={() => handleDelete(i)} color='secondary'>
                <DeleteIcon />
              </IconButton>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default List;
